	"Filled": {
		"Filled.js": "import React, { Component } from 'react';\nimport { Panel, Polar } from '@extjs/reactor/modern';\nimport ChartToolbar from '../../ChartToolbar';\nimport generateData from './generateData';\n\nexport default class Filled extends Component {\n\n    constructor() {\n        super();\n        this.refreshData();\n    }\n\n    store = Ext.create('Ext.data.Store', {\n        fields: ['id', 'g0', 'g1', 'g2', 'g3', 'g4', 'g5', 'g6', 'name']\n    });\n\n    state = {\n        numRecords: 15,\n        theme: 'default'\n    }\n\n    refreshData = () => {\n        this.store.loadData(generateData(this.state.numRecords));\n    }\n\n    changeTheme = theme => this.setState({ theme })\n\n    render() {\n        const { theme } = this.state;\n\n        return (\n            <Panel shadow layout=\"fit\">\n                <ChartToolbar\n                    onThemeChange={this.changeTheme}\n                    onRefreshClick={this.refreshData}\n                    theme={theme}\n                />\n                <Polar\n                    interactions=\"rotate\"\n                    legend={{ type: 'sprite' }}\n                    store={this.store}\n                    theme={theme}\n                    series={[{\n                        type: 'radar',\n                        title: 'G1',\n                        xField: 'id',\n                        yField: 'g1',\n                        style: {\n                            lineWidth: 4,\n                            fillOpacity: 0.3\n                        }\n                    }, {\n                        type: 'radar',\n                        title: 'G2',\n                        xField: 'id',\n                        yField: 'g2',\n                        style: {\n                            lineWidth: 4,\n                            fillOpacity: 0.3\n                        }\n                    }]}\n                    axes={[{\n                        type: 'numeric',\n                        position: 'radial',\n                        fields: ['g1', 'g2'],\n                        grid: true,\n                        style: {\n                            estStepSize: 20\n                        },\n                        limits: {\n                            value: 500,\n                            line: {\n                                strokeStyle: 'red',\n                                lineDash: [6, 3],\n                                title: {\n                                    text: 'Limit #1'\n                                }\n                            }\n                        }\n                    }, {\n                        type: 'category',\n                        position: 'angular',\n                        margin: 20,\n                        fields: 'id',\n                        grid: true,\n                        style: {\n                            estStepSize: 2\n                        },\n                        limits: [{\n                            value: 12,\n                            line: {\n                                strokeStyle: 'green',\n                                lineWidth: 3,\n                                lineDash: [6, 3],\n                                title: {\n                                    text: 'Limit #2',\n                                    fontSize: 14\n                                }\n                            }\n                        }, {\n                            value: 7,\n                            line: {\n                                strokeStyle: 'green',\n                                lineWidth: 3\n                            }\n                        }]\n                    }]}\n                />\n            </Panel>\n        )\n    }\n\n}",
		"ChartToolbar.js": "import React, { Component } from 'react';\nimport { Label, Spacer, Button, Toolbar, SegmentedButton } from '@extjs/reactor/modern';\n\nExt.require([\n    'Ext.chart.theme.Blue',\n    'Ext.chart.theme.Green',\n    'Ext.chart.theme.Muted',\n    'Ext.chart.theme.Red',\n    'Ext.chart.theme.Sky',\n    'Ext.chart.theme.Yellow'\n]);\n\nconst toolbarItemDefaults = {\n    margin: '0 0 0 10px'\n}\n\nconst downloadChart = (chart) => {\n    if(Ext.is.Desktop) {\n        chart.download({ filename: 'Chart' });\n    } else {\n        chart.preview();\n    }\n}\n    \nexport default function ChartToolbar({ \n    theme, \n    onThemeChange, \n    onToggleZoomOnPan, \n    onToggleCrosshair,\n    onRefreshClick,\n    downloadChartRef,\n    onStackGroup\n}) {\n    return (\n        <Toolbar docked=\"top\">\n            { theme && (\n                <Button \n                    iconCls=\"x-fa fa-picture-o\" \n                    text=\"Theme\"\n                    menu={[\n                        { text: 'Default', value: 'default', handler: () => onThemeChange('default') },\n                        { text: 'Green', value: 'green', handler: () => onThemeChange('green') },\n                        { text: 'Midnight', value: 'midnight', handler: () => onThemeChange('midnight') },\n                        { text: 'Muted', value: 'muted', handler: () => onThemeChange('muted') },\n                        { text: 'Red', value: 'red', handler: () => onThemeChange('red') },\n                        { text: 'Sky', value: 'sky', handler: () => onThemeChange('sky') },\n                        { text: 'Yellow', value: 'yellow', handler: () => onThemeChange('yellow') },\n                    ]}\n                />\n            )}\n            { downloadChartRef && (\n                <Button \n                    iconCls=\"x-fa fa-eye\" \n                    text=\"Preview\" \n                    handler={downloadChart.bind(null, downloadChartRef)}\n                    platformConfig={{\n                        desktop: {\n                            text: 'Download',\n                            iconCls: 'x-fa fa-download'\n                        }\n                    }}\n                />\n            )}\n            { onRefreshClick && (\n                <Button {...toolbarItemDefaults} iconCls=\"x-fa fa-refresh\" handler={onRefreshClick} text=\"Reload\"/>\n            )}\n            <Spacer/>\n            { onStackGroup && (\n                <SegmentedButton onToggle={onStackGroup}>\n                    <Button ui=\"default-toolbar\" text=\"Stack\" pressed/>\n                    <Button ui=\"default-toolbar\" text=\"Group\"/>\n                </SegmentedButton>\n            )}\n            { onToggleZoomOnPan && (\n                <SegmentedButton {...toolbarItemDefaults}>\n                    <Button ui=\"default-toolbar\" handler={() => onToggleZoomOnPan(false)} pressed text=\"Pan\"/>\n                    <Button ui=\"default-toolbar\" handler={() => onToggleZoomOnPan(true)} text=\"Zoom\"/>\n                    { onToggleCrosshair && <Button ui=\"default-toolbar\" handler={() => onToggleCrosshair(true)} text=\"Crosshair\"/> }\n                </SegmentedButton>\n            )}\n        </Toolbar>\n    )\n}\n",
		"generateData.js": "let seed = 1.3;\n\nconst random = () => {\n    seed *= 7.3;\n    seed -= Math.floor(seed);\n    return seed;\n};\n\nexport default function generateData(count) {\n    let record = {\n        id: 0,\n        g0: 300,\n        g1: 700 * random() + 100,\n        g2: 700 * random() + 100,\n        g3: 700 * random() + 100,\n        g4: 700 * random() + 100,\n        g5: 700 * random() + 100,\n        g6: 700 * random() + 100,\n        name: 'Item-0'\n    };\n    const data = [record];\n\n    for(let i=1; i<count; i++) {\n        data.push(record = {\n            id: i,\n            g0: record.g0 + 30 * random(),\n            g1: Math.abs(record.g1 + 300 * random() - 140),\n            g2: Math.abs(record.g2 + 300 * random() - 140),\n            g3: Math.abs(record.g3 + 300 * random() - 140),\n            g4: Math.abs(record.g4 + 300 * random() - 140),\n            g5: Math.abs(record.g5 + 300 * random() - 140),\n            g6: Math.abs(record.g6 + 300 * random() - 140),\n            name: 'Item-' + i\n        });\n    }\n\n    return data;\n}"
	},
